import { all, call, takeLatest, put } from 'redux-saga/effects';

import UserActionTypes from '../user/usertypes';
import { clearCart } from './cartactions';

import {
  getUserCartRef
} from '../../firebase/FirebaseUtils';


export function* clearCartOnSignOut() {
  yield put(clearCart());
}

export function* getCartFromFirebase({payload: user}) {
	try {
		const cartDocRef = yield call(getUserCartRef, user);
		const cartSnapshot = yield cartDocRef.get();
		return console.log(cartSnapshot.data());
	} catch (error) {
		console.log(error);
	}

}

export function* onSignOutSuccess() {
  yield takeLatest(UserActionTypes.SIGN_OUT_SUCCESS, clearCartOnSignOut);
}

export function* onUserSignIn() {
	yield takeLatest(UserActionTypes.SIGN_IN_SUCCESS, getCartFromFirebase);
}

export function* cartSagas() {
  yield all([call(onSignOutSuccess), call(onUserSignIn)]);
}
