import React from 'react';
import { Route } from 'react-router-dom';
import {createStructuredSelector} from 'reselect';
import {connect} from 'react-redux'


import {fetchCollectionsStartAsync} from '../../redux/shop/shopactions';
import {selectIsCollectionFetching, selectIsCollectionLoaded} from '../../redux/shop/shopselectors';

import WithSpinner from '../../components/withspinner/WithSpinner';

import CollectionsOverview from '../../components/collectionsoverview/CollectionsOverview';
import Collection from '../collection/Collection';

const CollectionsOverviewWithSpinner = WithSpinner(CollectionsOverview);
const CollectionsPageWithSpinner = WithSpinner(Collection);

class Shop extends React.Component {

	componentDidMount(){
		const {fetchCollectionsStartAsync} = this.props;
		fetchCollectionsStartAsync();
	}

  render() {
	const {isCollectionFetching, match, isCollectionLoaded} = this.props;

  return (
	<div className='shop-page'>
		<Route exact path={`${match.path}`} render={(props) => <CollectionsOverviewWithSpinner isLoading={isCollectionFetching} {...props}/>}/>
		<Route path={`${match.path}/:collectionId`} render={(props) => <CollectionsPageWithSpinner isLoading={!isCollectionLoaded} {...props}/>}/>
	</div>
	)}
};

const mapStateToProps = createStructuredSelector({
	isCollectionFetching: selectIsCollectionFetching,
	isCollectionLoaded: selectIsCollectionLoaded
});

const mapDispatchToProps = dispatch => ({
	fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync())
});



export default connect(mapStateToProps, mapDispatchToProps)(Shop);
