import React from 'react';
import { Route } from 'react-router-dom';
import {connect} from 'react-redux'

import { firestore, convertCollectionsSnapshotToMap } from '../../firebase/FirebaseUtils'

import {updateCollections} from '../../redux/shop/shopactions.js'

import WithSpinner from '../../components/withspinner/WithSpinner';

import CollectionsOverview from '../../components/collectionsoverview/CollectionsOverview';
import Collection from '../collection/Collection';

const CollectionsOverviewWithSpinner = WithSpinner(CollectionsOverview);
const CollectionsPageWithSpinner = WithSpinner(Collection);

class Shop extends React.Component {
state = {
	loading: true
};
	unsubscribeFromSnapshot = null;

	componentDidMount(){
		const {updateCollections} = this.props;
		const collectionRef = firestore.collection('collections');

		this.unsubscribeFromSnapshot = collectionRef.onSnapshot(async snapshot => {
			const collectionsMap = convertCollectionsSnapshotToMap(snapshot);
			updateCollections(collectionsMap);
			this.setState({loading: false});
		})
	}

  render() {
	const {match} = this.props;
	const {loading} = this.state;
  return (
	<div className='shop-page'>
		<Route exact path={`${match.path}`} render={(props) => <CollectionsOverviewWithSpinner isLoading={loading} {...props}/>}/>
		<Route path={`${match.path}/:collectionId`} render={(props) => <CollectionsPageWithSpinner isLoading={loading} {...props}/>}/>
	</div>
	)}
};

const mapDispatchToProps = dispatch => ({
	updateCollections: collectionsMap => dispatch(updateCollections(collectionsMap))
});



export default connect(null, mapDispatchToProps)(Shop);
