import CartActionTypes from './carttypes';

const INITIAL_STATE = {
	hidden: true
};

const cartReducer = (state = INITIAL_STATE, action) => {
	switch(action.type) {
		case CartActionTypes.TOGGLE_CART_HIDDEN:
			return {
				...state,
				hiddem : !state.hidden
			}
		default:
			return state;
	}
}

export default cartReducer;
